struct SocialItemBar<Site: PaletteWebsite>: Component {
    let context: PublishingContext<Site>
    
    var body: Component {
        List(context.site.socialItems) { item in
            Link(url: item.href) {
                SVG(path: item.type.svgPath, viewBox: item.type.viewBox).class("w-8 h-8")
            }
        }
        .class("flex")
    }
}
